import React, { FunctionComponent, useState } from "react";
import Pokemon from "../model/pokemon";
import './pokemon-card.css';

type Props = {
    pokemon: Pokemon,
    borderColor?: string
};

const PokemonCard: FunctionComponent<Props> = ({ pokemon, borderColor = '#009688' }) => {
   
    const [color, setColor] = useState<string>();

    const showBorder = () => {
        setColor(borderColor);
    }

    const hideBorder = () => {
        setColor('#f5f5f5'); // on remet la couleur en gris
    }

   const formattedDate = (date: Date): string => {
        return `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}`;
    }

    return (
        <div className="col s6 m4" onMouseOver={showBorder} onMouseOut={hideBorder}>
            <div className="card horizontal" style={{borderColor: color}}>
                <div className="card-image">
                    <img src={pokemon.picture} alt={pokemon.name} />
                </div>
                <div className="card-stacked">
                    <div className="card-content">
                        <p>{pokemon.name}</p>
                        <p><small>{formattedDate(pokemon.created)}</small></p>
                        {pokemon.types.map((type) => (
                            <span key={type} className="type-badge">{type}</span>
                        ))}
                    </div>
                </div>
            </div>
       </div>
    );
};

export default PokemonCard;
