import React, { FunctionComponent, useState } from 'react';
import Pokemon from '../model/pokemon';
import formatType from '../helpers/format-type';
import { useHistory } from 'react-router-dom';


type Props = {
    pokemon: Pokemon,

};

type Field = {
    value: any;
    error?: string;
    isValid?: boolean;
}

type Form = {
    name: Field;
    hp: Field;
    cp: Field;
    types: Field;
}

const PokemonForm: FunctionComponent<Props> = ({ pokemon }) => {

    const [form, setForm] = useState<Form>({
        name: { value: pokemon.name, isValid: true },
        hp: { value: pokemon.hp, isValid: true },
        cp: { value: pokemon.cp, isValid: true },
        types: { value: pokemon.types, isValid: true }
    });

    const types: string[] = ['Plante', 'Feu', 'Eau', 'Insecte', 'Normal', 'Electrik', 'Poison', 'Fée', 'Vol', 'Combat', 'Psy'];
    
    const hasType = (type: string): boolean => {
        return form.types.value.includes(type);
    }

    return (
        <form>
            <div className="row">
                <div className='col s12 m8 offset-m2'>
                    <div className="card hoverable">
                        <div className="card-image">
                            <img src={pokemon.picture} alt={pokemon.name} style={{ width: '250px', margin: '0 auto' }}/>
                        </div>
                        <div className="card-stacked">
                            <div className="card-content">
                                {/* pokemon name */}
                                <div className="form-group">
                                    <label htmlFor="name">Nom</label>
                                    <input id="name" type="text" className="form-control" value={pokemon.name} />
                                </div>
                                {/* pokemon hp */}
                                <div className="form-group">
                                    <label htmlFor="hp">Points de vie</label>
                                    <input id="hp" type="number" className="form-control" value={pokemon.hp} />
                                </div>
                                {/* pokemon cp */}
                                <div className="form-group">
                                    <label htmlFor="cp">Dégâts</label>  
                                    <input id="cp" type="number" className="form-control" value={pokemon.cp} />
                                </div>
                                {/* pokemon types */}
                                <div className="form-group">
                                    <label>Types</label>
                                    {types.map(type => (
                                        <div key={type} style={{ marginBottom: '10px' }}>
                                            <label>
                                                <input id={type} type="checkbox" className="filled-in" value={type} checked={hasType(type)} />
                                                <span >
                                                    <p className={formatType(type)}>{type}</p>
                                                </span>
                                            </label>
                                        </div>
                                    ))}
                                </div>
                            </div>
                            <div className="card-action center">
                                {/* Bouton de soumission du formulaire */}
                                <button type="submit" className="btn btn-primary">Sauvegarder</button>
                            </div>
                        </div>
                </div>
                </div>
                </div>
        </form>
    );
};
export default PokemonForm;
